import{_ as i,c as o,o as r,ag as t}from"./chunks/framework.DPDPlp3K.js";const h=JSON.parse('{"title":"Introduction","description":"","frontmatter":{"prev":{"text":"Recommender Systems","link":"/recommender-systems/index"},"next":false},"headers":[],"relativePath":"recommender-systems/introduction.md","filePath":"recommender-systems/introduction.md"}'),n={name:"recommender-systems/introduction.md"};function a(l,e,s,m,c,d){return r(),o("div",null,e[0]||(e[0]=[t('<h1 id="introduction" tabindex="-1">Introduction <a class="header-anchor" href="#introduction" aria-label="Permalink to &quot;Introduction&quot;">​</a></h1><h2 id="history" tabindex="-1">History <a class="header-anchor" href="#history" aria-label="Permalink to &quot;History&quot;">​</a></h2><ul><li>Ants, cavemen, and Early Recommender Systems <ul><li>The emergence of critics</li></ul></li><li>Information Retrieval and Filtering</li><li>Manual Collaborative Filtering</li><li>Automated Collaborative Filtering</li><li>The Commercial Era</li></ul><h2 id="information-retrieval-and-filtering" tabindex="-1">Information Retrieval and Filtering <a class="header-anchor" href="#information-retrieval-and-filtering" aria-label="Permalink to &quot;Information Retrieval and Filtering&quot;">​</a></h2><h3 id="information-retrieval" tabindex="-1">Information Retrieval <a class="header-anchor" href="#information-retrieval" aria-label="Permalink to &quot;Information Retrieval&quot;">​</a></h3><ul><li><p>Static content base<br> Invest time in indexing content</p></li><li><p>Dynamic information need<br> Queries presented in &quot;real time&quot;</p></li><li><p>Common approach: TFIDF (Term Frequency-Inverse Document Frequency)<br> Rank documents by term overlap<br> Rank terms by frequency</p></li><li><p>Problem: The stream of information changes rapidly, but people only need the information related to their needs.<br> (Information retrieval were reversed)</p></li></ul><h3 id="information-filtering" tabindex="-1">Information Filtering <a class="header-anchor" href="#information-filtering" aria-label="Permalink to &quot;Information Filtering&quot;">​</a></h3><ul><li>Reverse assumptions from IR<br> Static information need<br> Dynamic content base</li><li>Invest effort in modeling user need<br> Hand-created &quot;profile&quot;<br> Machine learned profile<br> Feedback/updates</li><li>Pass new content through filters</li></ul><h2 id="manual-collaborative-filtering-cf" tabindex="-1">Manual Collaborative Filtering (CF) <a class="header-anchor" href="#manual-collaborative-filtering-cf" aria-label="Permalink to &quot;Manual Collaborative Filtering (CF)&quot;">​</a></h2><ul><li>Premise (前提)<br> Information need more complex than keywords or topics: quality and taste</li><li>Small Community: Manual<br> Tapestry: database of content and comments<br> Active CF: easy mechanisms for forwarding content to relevant readers</li></ul><h2 id="recommendation-approaches" tabindex="-1">Recommendation Approaches <a class="header-anchor" href="#recommendation-approaches" aria-label="Permalink to &quot;Recommendation Approaches&quot;">​</a></h2><ul><li>Non-Personalized and Stereotyped<br> Popularity, Group Preference</li><li>Product Association<br> People who like/bought X, also like Y</li><li>Content-Based<br> Learn what I like (attributes)</li><li>Collaborative<br> Learn what I like: use others&#39; experience to recommend (multiple ways)</li></ul><h2 id="preferences-and-ratings" tabindex="-1">Preferences and Ratings <a class="header-anchor" href="#preferences-and-ratings" aria-label="Permalink to &quot;Preferences and Ratings&quot;">​</a></h2><ul><li>Explicit (the preference got from asking users to give comments)<br> Rating<br> Review<br> Vote</li><li>Implicit (the preference got by users&#39; actions)<br> Click<br> Purchase<br> Follow</li></ul><h2 id="predictions-and-recommendations" tabindex="-1">Predictions and Recommendations <a class="header-anchor" href="#predictions-and-recommendations" aria-label="Permalink to &quot;Predictions and Recommendations&quot;">​</a></h2><ul><li>Prediction <ul><li>Estimates of how much user will like an item<br> Often scaled to match some rating scale<br> Often tied to search or browsing for specific products</li><li>Pro: helps quantify item</li><li>Con: provides something falsifiable (证伪的）</li></ul></li><li>Recommendations <ul><li>Suggestions for items user might like (or might fit what they are doing)<br> Often presented in the form of &quot;top-n&quot; list<br> Also sometimes just placed in front of the user</li><li>Pro: provides good choices as a default</li><li>Con: if perceived as top-n, can result in failure to explore (when the top few seem poor)</li></ul></li><li>Explicitly(直白地) and Organically(自然逐步地) <ul><li>Explicit recommend: recommend items directly</li><li>Organic recommend: recommend items gradually</li><li>Historical: Just for you (show the recommendation directly)</li><li>Today:<br> Balance between explicit prediction(falsifiable) and coarser granularity(粗粒度)<br> Balance between top-n and softer presentation(might be interesting)</li></ul></li></ul><h2 id="taxonomy-分类学-of-recommenders" tabindex="-1">Taxonomy (分类学) of Recommenders <a class="header-anchor" href="#taxonomy-分类学-of-recommenders" aria-label="Permalink to &quot;Taxonomy (分类学) of Recommenders&quot;">​</a></h2><ul><li>Dimensions of Analysis of Recommender System <ul><li><p>Domain</p><ul><li>Content to Commerce and Beyond<br> News, information, &quot;text&quot;<br> Products, vendors, bundles<br> Matchmaking (other people)<br> Sequences (e.g., music playlists)</li><li>One particularly interesting property<br> New items (e.g., movies, books, ...)<br> Re-recommend old ones (e.g., groceries, music)</li></ul></li><li><p>Purpose</p><ul><li>The recommendations themselves<br> Sales<br> Information</li><li>Education of user/customer</li><li>Build a community of users/customers around products or content</li></ul></li><li><p>Recommendation Context</p><ul><li>The user action when getting recommendation</li><li>The constrain approach of context to the recommender<br> Groups<br> Automatic consumption (vs. suggestion)<br> Level of attention<br> Level of interruption</li></ul></li><li><p>Whose Opinions (Data)<br> &quot;Experts&quot;<br> Ordinary &quot;phoaks&quot;<br> Normal people</p></li><li><p>Personalization Level</p></li><li><p>Privacy and Trustworthiness</p></li><li><p>Interfaces</p></li><li><p>Recommendation Algorithms</p></li></ul></li></ul>',18)]))}const p=i(n,[["render",a]]);export{h as __pageData,p as default};
